import React, { useState, useEffect } from 'react';
import {
    Box,
    Button,
    Card,
    CardBody,
    FormControl,
    FormLabel,
    Heading,
    Input,
    Link,
    Modal,
    ModalBody,
    ModalCloseButton,
    ModalContent,
    ModalFooter,
    ModalHeader,
    ModalOverlay,
    Stack,
    Text,
    Textarea,
    useDisclosure,
    useToast,
    Tabs,
    TabList,
    TabPanels,
    Tab,
    TabPanel,
    extendTheme,
    ChakraProvider,
    Select,
} from '@chakra-ui/react';
import axios from 'axios';
import DatePicker from 'react-datepicker';
import 'react-datepicker/dist/react-datepicker.css';

const theme = extendTheme({
    styles: {
        global: {
            body: {
                color: 'white',
                bg: 'black',
            },
        },
    },
});

const EventsDiscovery = () => {
    const [eventsByCategory, setEventsByCategory] = useState({});
    const [newEvent, setNewEvent] = useState({
        category: '',
        title: '',
        description: '',
        time: new Date(),
        place: '',
        rsvp: '',
    });
    const [showForm, setShowForm] = useState(false);
    const [selectedCategory, setSelectedCategory] = useState('');
    const { isOpen, onOpen, onClose } = useDisclosure();
    const toast = useToast();

    useEffect(() => {
        // Fetch events from API
        axios.get('http://localhost:3001/events/all')
            .then(response => {
                const events = response.data;
                const categorizedEvents = events.reduce((acc, event) => {
                    if (!acc[event.category]) acc[event.category] = [];
                    acc[event.category].push(event);
                    return acc;
                }, {});
                setEventsByCategory(categorizedEvents);
            })
            .catch(error => console.error('Error fetching events:', error));
    }, []);

    const handleChange = (e) => {
        const { name, value } = e.target;
        setNewEvent(prevState => ({ ...prevState, [name]: value }));
    };

    const handleDateChange = (date) => {
        setNewEvent(prevState => ({ ...prevState, time: date }));
    };

    const handleSubmit = () => {
        axios.post('http://localhost:3001/events/add', {
            ...newEvent,
            time: newEvent.time.toISOString(), // Ensure the date is in ISO format
        })
            .then(response => {
                // Update local state
                setEventsByCategory(prevState => {
                    const updatedCategory = prevState[newEvent.category] || [];
                    return {
                        ...prevState,
                        [newEvent.category]: [...updatedCategory, response.data],
                    };
                });
                // Show success message
                toast({
                    title: 'Event posted.',
                    description: 'Your event has been successfully posted.',
                    status: 'success',
                    duration: 3000,
                    isClosable: true,
                });
                // Reset form
                setNewEvent({
                    category: '',
                    title: '',
                    description: '',
                    time: new Date(),
                    place: '',
                    rsvp: '',
                });
                setShowForm(false);
            })
            .catch(error => {
                console.error('Error posting event:', error);
                toast({
                    title: 'An error occurred.',
                    description: 'Unable to post the event. Please try again later.',
                    status: 'error',
                    duration: 3000,
                    isClosable: true,
                });
            });
    };

    return (
        <ChakraProvider theme={theme}>
            <Box p={4}>
                <Heading fontFamily='Montserrat, sans-serif' fontWeight='bold' color='white' fontSize='50px' textAlign='center' mt={8} mb={4}>
                    Events
                </Heading>
                <Button onClick={() => { setShowForm(true); onOpen(); }} colorScheme='purple' mb={4}>
                    Add New Event
                </Button>
                <Tabs variant='soft-rounded'>
                    <TabList bg='purple.200' borderRadius='xl' p={4} mb={4}>
                        {Object.keys(eventsByCategory).map((category, index) => (
                            <Tab
                                key={index}
                                _selected={{ color: 'white', bg: 'purple.700' }}
                                _focus={{ boxShadow: 'none' }}
                                _hover={{ bg: 'purple.500' }}
                                mr={2}
                                onClick={() => setSelectedCategory(category)}
                            >
                                {category}
                            </Tab>
                        ))}
                    </TabList>
                    <TabPanels>
                        {Object.keys(eventsByCategory).map((category, index) => (
                            <TabPanel key={index}>
                                <div>
                                    {eventsByCategory[category]?.length > 0 ? (
                                        eventsByCategory[category].map((event, i) => (
                                            <Card
                                                key={i}
                                                direction={{ base: 'column', sm: 'row' }}
                                                overflow='hidden'
                                                variant='elevated'
                                                bg="rgba(255, 255, 255, 0.8)"
                                                maxW="97%"
                                                padding="20px"
                                                borderRadius={"50px"}
                                                mb={4}
                                            >
                                                <Stack>
                                                    <CardBody padding="10px">
                                                        <Heading size='lg'>{event.title}</Heading>
                                                        <Text py='4' fontSize="18px" fontFamily={'Montserrat, sans-serif'} fontWeight={"500"}>
                                                            {event.description}
                                                        </Text>
                                                        <Text fontSize="16px" fontFamily={'Montserrat, sans-serif'} fontWeight={"500"}>
                                                            <strong>Time:</strong> {new Date(event.time).toLocaleString()}
                                                        </Text>
                                                        <Text fontSize="16px" fontFamily={'Montserrat, sans-serif'} fontWeight={"500"}>
                                                            <strong>Place:</strong> {event.place}
                                                        </Text>
                                                        <Link href={event.rsvp} color='purple.500' isExternal>
                                                            RSVP Here
                                                        </Link>
                                                    </CardBody>
                                                </Stack>
                                            </Card>
                                        ))
                                    ) : (
                                        <h3 style={{ color: 'white' }}>No events scheduled.</h3>
                                    )}
                                </div>
                            </TabPanel>
                        ))}
                    </TabPanels>
                </Tabs>

                <Modal isOpen={isOpen} onClose={onClose}>
                    <ModalOverlay />
                    <ModalContent>
                        <ModalHeader color="black">Create New Event</ModalHeader>
                        <ModalCloseButton />
                        <ModalBody color="black">
                            <FormControl mb={4}>
                                <FormLabel>Category</FormLabel>
                                <Select name="category" value={newEvent.category} onChange={handleChange} placeholder="Select Category">
                                    <option value="Dance">Dance</option>
                                    <option value="Music - Classical">Music - Classical</option>
                                    <option value="Music - Contemporary">Music - Contemporary</option>
                                    <option value="Visual Art">Visual Art</option>
                                    <option value="Theatre">Theatre</option>
                                    <option value="Film & Literature">Film & Literature</option>
                                </Select>
                            </FormControl>
                            <FormControl mb={4}>
                                <FormLabel>Title</FormLabel>
                                <Input name="title" value={newEvent.title} onChange={handleChange} placeholder="Event Title" />
                            </FormControl>
                            <FormControl mb={4}>
                                <FormLabel>Description</FormLabel>
                                <Textarea name="description" value={newEvent.description} onChange={handleChange} placeholder="Event Description" />
                            </FormControl>
                            <FormControl mb={4}>
                                <FormLabel>Time</FormLabel>
                                <DatePicker
                                    selected={newEvent.time}
                                    onChange={handleDateChange}
                                    showTimeSelect
                                    dateFormat="Pp"
                                    customInput={<Input placeholder="Select Date & Time" />}
                                />
                            </FormControl>
                            <FormControl mb={4}>
                                <FormLabel>Place</FormLabel>
                                <Input name="place" value={newEvent.place} onChange={handleChange} placeholder="Event Place" />
                            </FormControl>
                            <FormControl mb={4}>
                                <FormLabel>RSVP Link</FormLabel>
                                <Input name="rsvp" value={newEvent.rsvp} onChange={handleChange} placeholder="RSVP Link" />
                            </FormControl>
                        </ModalBody>
                        <ModalFooter>
                            <Button colorScheme="purple" mr={3} onClick={handleSubmit}>
                                Submit
                            </Button>
                            <Button variant="outline" onClick={onClose}>
                                Cancel
                            </Button>
                        </ModalFooter>
                    </ModalContent>
                </Modal>
            </Box>
        </ChakraProvider>
    );
};

export default EventsDiscovery;
