import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Box, Button, Input, Image, Text, VStack } from '@chakra-ui/react';

const PortfolioManager = ({ userId }) => {
    const [file, setFile] = useState(null);
    const [imagePreview, setImagePreview] = useState(null);
    const [searchTerm, setSearchTerm] = useState('');
    const [userPortfolios, setUserPortfolios] = useState([]);
    //  const [userId, setUserId] = useState(1); // Replace with dynamic user ID or get from context/session

    const handleFileChange = (event) => {
        const selectedFile = event.target.files[0];
        const reader = new FileReader();

        reader.onloadend = () => {
            setFile(reader.result);
            setImagePreview(URL.createObjectURL(selectedFile));
        };

        if (selectedFile) {
            reader.readAsDataURL(selectedFile);
        }
    };

    const handleUpload = async () => {
        try {
            const response = await axios.post('http://localhost:3001/portfolio/upload', {
                image: file,
                userId: userId
            });
            console.log('Image uploaded successfully:', response.data);
        } catch (error) {
            console.error('Error uploading image:', error);
        }
    };

    const handleSearch = async () => {
        try {
            const response = await axios.get('http://localhost:3001/portfolio/search', {
                params: { searchTerm }
            });
            setUserPortfolios(response.data);
        } catch (error) {
            console.error('Error fetching portfolios:', error);
        }
    };

    useEffect(() => {
        if (searchTerm) {
            handleSearch();
        } else {
            setUserPortfolios([]);
        }
    }, [searchTerm]);

    return (
        <VStack spacing={4} align="stretch">
            <Box borderWidth="1px" borderRadius="lg" overflow="hidden" p={4}>
                <Text fontWeight="bold">Upload Your Portfolio Image</Text>
                <Input type="file" onChange={handleFileChange} />
                {imagePreview && <Image src={imagePreview} alt="Preview" boxSize="200px" objectFit="cover" />}
                <Button onClick={handleUpload} colorScheme="teal">Upload</Button>
            </Box>

            <Box borderWidth="1px" borderRadius="lg" overflow="hidden" p={4}>
                <Input
                    placeholder="Search for users..."
                    value={searchTerm}
                    onChange={(e) => setSearchTerm(e.target.value)}
                />
                <Button onClick={handleSearch} colorScheme="teal">Search</Button>
            </Box>

            <VStack spacing={4}>
                {userPortfolios.map((portfolio) => (
                    <Box key={portfolio.userId} borderWidth="1px" borderRadius="lg" overflow="hidden" p={4}>
                        <Text fontWeight="bold">{portfolio.FirstName} {portfolio.LastName}</Text>
                        <Image src={portfolio.ImageUrl} alt="Portfolio Image" boxSize="200px" objectFit="cover" />
                    </Box>
                ))}
            </VStack>
        </VStack>
    );
};

export default PortfolioManager;
