// components/AboutUs.js
import React from 'react';
import { Box, Divider, AbsoluteCenter } from '@chakra-ui/react';
import { Card, Image, Stack, CardBody, CardFooter, Heading, Text, Button } from '@chakra-ui/react';

const AboutUs = () => {
    return (
        <Box position='relative' padding='10' mt='150'>
            <Divider borderColor="gray.300" />
            <AbsoluteCenter px='4'>
                <span style={{ fontFamily: 'Montserrat, sans-serif', fontWeight: 'bold', fontSize: 50, color: 'white' }}> About Us</span>
            </AbsoluteCenter>

            <Card direction={{ base: 'column', sm: 'row' }} overflow='hidden' variant='outline' marginTop="5">
                <Image
                    objectFit='cover'
                    maxW={{ base: '100%', sm: '200px' }}
                    src='https://images.unsplash.com/photo-1667489022797-ab608913feeb?ixlib=rb-4.0.3&ixid=MnwxMjA3fDB8MHxlZGl0b3JpYWwtZmVlZHw5fHx8ZW58MHx8fHw%3D&auto=format&fit=crop&w=800&q=60'
                    alt='Caffe Latte'
                />

                <Stack>
                    <CardBody>
                        <Heading size='md'>The perfect latte</Heading>
                        <Text py='2'>
                            Caff√® latte is a coffee beverage of Italian origin made with espresso and steamed milk.
                        </Text>
                    </CardBody>

                    <CardFooter>
                        <Button variant='solid' colorScheme='blue'>
                            Buy Latte
                        </Button>
                    </CardFooter>
                </Stack>
            </Card>
        </Box>
    );
};

export default AboutUs;
