import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Box, Button, Input, Image, VStack, Text, SimpleGrid } from '@chakra-ui/react';

const PortfolioManager = ({ userId }) => {
    const [file, setFile] = useState(null);
    const [imagePreview, setImagePreview] = useState(null);
    const [searchTerm, setSearchTerm] = useState('');
    const [portfolios, setPortfolios] = useState([]);
    const [userPortfolio, setUserPortfolio] = useState([]);

    // Handle file change and image preview
    const handleFileChange = (event) => {
        const selectedFile = event.target.files[0];
        const reader = new FileReader();

        reader.onloadend = () => {
            setFile(reader.result); // Base64 image string with prefix
            setImagePreview(URL.createObjectURL(selectedFile));
        };

        if (selectedFile) {
            reader.readAsDataURL(selectedFile);
        }
    };

    const handleUpload = async () => {
        try {
            const base64Image = file; // Already includes 'data:image/jpeg;base64,' prefix
            const response = await axios.post('http://localhost:3001/portfolio/upload', {
                userId,
                imageBase64: base64Image
            });
            console.log('Image uploaded successfully:', response.data);
            setFile(null);
            setImagePreview(null);
            fetchUserPortfolio(); // Refresh user's portfolio after upload
        } catch (error) {
            console.error('Error uploading image:', error);
        }
    };

    // Fetch user's own portfolio
    const fetchUserPortfolio = async () => {
        try {
            const response = await axios.get('http://localhost:3001/portfolio/myportfolio', {
                params: { userId }
            });
            setUserPortfolio(response.data);
        } catch (error) {
            console.error('Error fetching user portfolio:', error);
        }
    };

    // Search portfolios based on search term
    const handleSearch = async () => {
        try {
            const response = await axios.get('http://localhost:3001/portfolio/search', {
                params: { searchTerm }
            });
            setPortfolios(response.data);
        } catch (error) {
            console.error('Error fetching portfolios:', error);
        }
    };

    // Trigger search when search term changes
    useEffect(() => {
        if (searchTerm) {
            handleSearch();
        } else {
            setPortfolios([]);
        }
    }, [searchTerm]);

    // Fetch user portfolio on component mount
    useEffect(() => {
        fetchUserPortfolio();
    }, []);

    return (
        <VStack spacing={4} align="stretch">
            {/* Display User's Portfolio */}
            <Text fontSize="2xl" fontWeight="bold">My Portfolio</Text>
            <SimpleGrid columns={{ base: 1, md: 3 }} spacing={4}>
                {userPortfolio.map((image, index) => (
                    <Box key={index} borderWidth="1px" borderRadius="lg" overflow="hidden" p={4}>
                        <Image src={image.ImageData} alt="User Portfolio Image" boxSize="200px" objectFit="cover" />
                    </Box>
                ))}
            </SimpleGrid>

            {/* Image Upload Section */}
            <Input type="file" accept="image/*" onChange={handleFileChange} />
            {imagePreview && <Image src={imagePreview} alt="Preview" boxSize="200px" objectFit="cover" />}
            <Button onClick={handleUpload} colorScheme="teal" disabled={!file}>Upload</Button>

            {/* Search Section */}
            <Input
                placeholder="Search for portfolios..."
                value={searchTerm}
                onChange={(e) => setSearchTerm(e.target.value)}
            />
            <Button onClick={handleSearch} colorScheme="teal">Search</Button>

            {/* Display Portfolios */}
            <SimpleGrid columns={{ base: 1, md: 3 }} spacing={4}>
                {portfolios.map((portfolio) => (
                    <Box key={portfolio.userId} borderWidth="1px" borderRadius="lg" overflow="hidden" p={4}>
                        <Text fontWeight="bold">{portfolio.FirstName} {portfolio.LastName}</Text>
                        <Image src={portfolio.ImageData} alt="Portfolio Image" boxSize="200px" objectFit="cover" />
                    </Box>
                ))}
            </SimpleGrid>
        </VStack>
    );
};

export default PortfolioManager;
