import React, { useState } from 'react';
import Button from '@mui/material/Button';
import Avatar from '@mui/material/Avatar';
import EventsDiscovery from './EventsDiscovery'; // Assuming this is your component for events discovery

function HomePage() {
    const [userName, setUserName] = useState('');
    const [currentPage, setCurrentPage] = useState(null); // State to track current page/component

    const handleEventsClick = () => {
        setCurrentPage('events');
    };

    const handleMessagesClick = () => {
        setCurrentPage('messages');
    };

    const handleConnectionsClick = () => {
        setCurrentPage('connections');
    };

    const handleJobClick = () => {
        setCurrentPage('job');
    };

    // Example useEffect for fetching user data
    /*
    useEffect(() => {
        axios.get('http://localhost:3001')
            .then(response => {
                setUserName(response.data.firstName);  // Set the user's first name to state
            })
            .catch(error => {
                console.error('Error fetching user data:', error);  // Handling errors
            });
    }, []);
    */

    // Function to render current page based on state
    const renderPage = () => {
        switch (currentPage) {
            case 'events':
                return <EventsDiscovery />;
            // Add cases for other pages/components as needed
            default:
                return (
                    <>
                        <div style={{ backgroundColor: '#f2f2f2', minHeight: '100vh' }}>
                            <div style={{ textAlign: 'center', color: '#333', fontFamily: 'Montserrat, sans-serif', display: 'flex', flexDirection: 'column', alignItems: 'center' }}>
                                <Avatar
                                    style={{
                                        justifyContent: 'center',
                                        height: '100px',
                                        width: '100px',
                                        background: '#9C27B0',
                                        fontSize: '3 rem'
                                    }}>
                                    H
                                </Avatar>
                                <h1 style={{ fontWeight: 'bold' }}>Welcome to the Home Page! {userName}</h1>
                                <h2>You have successfully logged in!</h2>
                                <p>Feel free to enjoy the following fun features!</p>
                                <br />
                            </div>
                            <div style={{ display: 'flex', flexDirection: 'row', gap: '20px' }}>
                                <Button
                                    variant='outlined'
                                    style={{
                                        fontSize: '1.7rem',
                                        borderRadius: '50px',
                                        fontFamily: 'Montserrat, sans-serif',
                                        color: '#3900FF',
                                        borderColor: '#3900FF'
                                    }}
                                    onClick={handleEventsClick}
                                >
                                    Events Discovery
                                </Button>
                                <Button
                                    variant='outlined'
                                    style={{
                                        fontSize: '1.7rem',
                                        borderRadius: '50px',
                                        fontFamily: 'Montserrat, sans-serif',
                                        color: '#9C27B0',
                                        borderColor: '#9C27B0'
                                    }}
                                    onClick={handleJobClick}
                                >
                                    Job Openings
                                </Button>
                                <Button
                                    variant='outlined'
                                    style={{
                                        fontSize: '1.7rem',
                                        borderRadius: '50px',
                                        fontFamily: 'Montserrat, sans-serif',
                                        color: '#9200FF',
                                        borderColor: '#9200FF'
                                    }}
                                    onClick={handleMessagesClick}
                                >
                                    Messages
                                </Button>
                                <Button
                                    variant='outlined'
                                    style={{
                                        fontSize: '1.7rem',
                                        borderRadius: '50px',
                                        fontFamily: 'Montserrat, sans-serif',
                                        color: '#FF0097',
                                        borderColor: '#FF0097'
                                    }}
                                    onClick={handleConnectionsClick}
                                >
                                    Connections
                                </Button>
                            </div>
                            <div style={{ display: 'flex', flexDirection: 'row', gap: '20px', fontFamily: 'Montserrat, sans-serif' }}>
                                <p> Here is where you can customize your profile, or add relevant skills. Do remember this profile will be displayed to other users and could potentially affect your hireability.</p>
                            </div>
                        </div>
                    </>
                );
        }
    };

    return (
        <main style={{ padding: '0 20px', minHeight: '100vh', display: 'flex', flexDirection: 'column', justifyContent: 'center', alignItems: 'center' }}>
            {renderPage()}
        </main>
    );
}

export default HomePage;
