import React, { useState, useEffect } from 'react';
import Button from '@mui/material/Button';
import Avatar from '@mui/material/Avatar';
import axios from 'axios';
import { ChakraProvider } from '@chakra-ui/react';
import ConnectionForm from './ConnectionForm';
import Messages from './Messages';
import EventsDiscovery from './EventsDiscovery';
import JobOpenings from './JobOpenings';
import PendingRequests from './PendingRequests';
import './HomePage.css'; // Import CSS for styling

const HomePage = ({ user }) => {
    const [currentPage, setCurrentPage] = useState(null);
    const [chats, setChats] = useState([]);
    const [selectedChat, setSelectedChat] = useState(null);
    const [messages, setMessages] = useState([]);

    useEffect(() => {
        if (currentPage === 'messages') {
            fetchChats();
        }
    }, [currentPage]);

    const fetchChats = async () => {
        try {
            const response = await axios.get(`http://localhost:3001/chats/${user.id}`);
            setChats(response.data);
            if (response.data.length > 0) {
                setSelectedChat(response.data[0]);
            }
        } catch (error) {
            console.error('Error fetching chats:', error);
        }
    };

    const handleConnectionsClick = () => {
        setCurrentPage('connections');
    };

    const handleEventsClick = () => {
        setCurrentPage('events');
    };

    const handleJobClick = () => {
        setCurrentPage('job');
    };

    const handleMessagesClick = () => {
        setCurrentPage('messages');
    };

    const renderPage = () => {
        switch (currentPage) {
            case 'events':
                return (
                    <>
                        <Button
                            variant="outlined"
                            style={{ backgroundColor: '#9C27B0', color: 'white', borderColor: '#6A1B9A', marginLeft: '20px' }}
                            onClick={() => setCurrentPage(null)}
                        >
                            Back to Home
                        </Button>
                        <ChakraProvider>
                            <EventsDiscovery />
                        </ChakraProvider>
                    </>
                );
            case 'job':
                return (
                    <>
                        <Button
                            variant="outlined"
                            style={{ backgroundColor: '#9C27B0', color: 'white', borderColor: '#6A1B9A', marginLeft: '20px' }}
                            onClick={() => setCurrentPage(null)}
                        >
                            Back to Home
                        </Button>
                        <ChakraProvider>
                            <JobOpenings />
                        </ChakraProvider>
                    </>
                );
            case 'messages':
                return (
                    <>
                        <Button
                            variant="outlined"
                            style={{ backgroundColor: '#9C27B0', color: 'white', borderColor: '#6A1B9A', marginLeft: '20px' }}
                            onClick={() => setCurrentPage(null)}
                        >
                            Back to Home
                        </Button>
                        <Messages userId={user.id} chats={chats} selectedChat={selectedChat} setSelectedChat={setSelectedChat} />
                    </>
                );
            case 'connections':
                return (
                    <>
                        <Button
                            variant="outlined"
                            style={{ backgroundColor: '#9C27B0', color: 'white', borderColor: '#6A1B9A', marginLeft: '20px' }}
                            onClick={() => setCurrentPage(null)}
                        >
                            Back to Home
                        </Button>
                        <ConnectionForm senderEmail={user.email} />
                        <PendingRequests userEmail={user.email} />
                    </>
                );
            default:
                return (
                    <>
                        <div className="home-page-container">
                            <div className="welcome-section">
                                <Avatar src="/avatar.png" sx={{ height: '250px', width: '250px', marginRight: '10vw' }} />
                                <div className="welcome-text">
                                    <h1>Welcome back {user ? `${user.firstName} ${user.lastName}` : ''}!</h1>
                                    <h2>You have successfully logged in!</h2>
                                    <p>Feel free to enjoy the following fun features.</p>
                                </div>
                            </div>
                            <hr color="#c1acd9" width="90%" />
                            <div className="feature-section">
                                <div className="feature-icons">
                                    <img src="/events.png" alt="Events" />
                                    <img src="/jobs.png" alt="Jobs" />
                                    <img src="/messages.png" alt="Messages" />
                                    <img src="/connections.png" alt="Connections" />
                                </div>
                                <div className="feature-buttons">
                                    <Button variant='outlined' onClick={handleEventsClick}>Events Discovery</Button>
                                    <Button variant='outlined' onClick={handleJobClick}>Job Openings</Button>
                                    <Button variant='outlined' onClick={handleMessagesClick}>Messages</Button>
                                    <Button variant='outlined' onClick={handleConnectionsClick}>Connections</Button>
                                </div>
                            </div>
                            <div className="feature-description">
                                <p>Explore features to message other artists or potential employers, connect with others, apply for jobs, and discover events!</p>
                            </div>
                        </div>
                    </>
                );
        }
    };

    return <>{renderPage()}</>;
};

export default HomePage;
