import React, { useState, useEffect } from 'react';
import axios from 'axios';

const Messages = ({ userId, contactId }) => {
    const [messages, setMessages] = useState([]);
    const [newMessage, setNewMessage] = useState('');

    useEffect(() => {
        const fetchMessages = async () => {
            try {
                const response = await axios.get(`http://localhost:3001/messages/${userId}/${contactId}`);
                setMessages(response.data);
            } catch (error) {
                console.error('Error fetching messages:', error);
                // Handle error (e.g., show error message to user)
            }
        };
        fetchMessages();
    }, [userId, contactId]);

    const sendMessage = async () => {
        try {
            await axios.post('http://localhost:3001/messages/send', {
                senderId: userId,
                receiverId: contactId,
                message: newMessage
            });
            setNewMessage('');
            // Re-fetch messages after sending a new one
            const response = await axios.get(`http://localhost:3001/messages/${userId}/${contactId}`);
            setMessages(response.data);
        } catch (error) {
            console.error('Error sending message:', error);
            // Handle error (e.g., show error message to user)
        }
    };

    return (
        <div>
            <div>
                {messages.map(msg => (
                    <div key={msg.id}>
                        <strong>{msg.senderId === userId ? 'You' : 'Contact'}:</strong> {msg.message}
                    </div>
                ))}
            </div>
            <div>
                <input
                    type="text"
                    value={newMessage}
                    onChange={(e) => setNewMessage(e.target.value)}
                    placeholder="Type a message"
                />
                <button onClick={sendMessage}>Send</button>
            </div>
        </div>
    );
};

export default Messages;
