import { useState, React, useEffect } from 'react'
import axios from "axios";

const Messages = () => {
    const [messages, setMessages] = useState([]);
    const [inputText, setInputText] = useState('');

    return (
        <div>
            <div className="menu" style={{ backgroundColor: "white", display: "flex", justifyContent: "center", width: "25%", height: "100vh", borderRadius: "20px", opacity: ".9" }}>
                <h2 style={{ fontFamily: 'Montserrat, sans-serif' }}>Messages</h2>
            </div>
            <div className="message-list" style={{ backgroundColor: "white", display: "flex", justifyContent: "flex-end", width: "60%", height: "100vh", borderRadius: "20px", opacity: ".9" }}>
                {messages.map(message => (
                    <div key={message.id} className="message">
                        <strong>{message.sender}</strong>: {message.text}
                    </div>
                ))}
            </div>
            <form>
                <input
                    type="text"
                    value={inputText}
                    onChange={(e) => setInputText(e.target.value)}
                    placeholder="Type your message..."
                />
                <button type="submit">Send</button>
            </form>
        </div>
    )

}

export default Messages;
