import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './Messages.css'; // Import CSS file for styling

const Messages = ({ userId, contacts, selectedContact, setSelectedContact }) => {
    const [messages, setMessages] = useState([]);
    const [newMessage, setNewMessage] = useState('');
    const [searchTerm, setSearchTerm] = useState('');
    const [existingChats, setExistingChats] = useState(new Set());

    useEffect(() => {
        if (selectedContact) {
            fetchMessages(selectedContact.id);
            addChatToExisting(selectedContact.id);
        }
    }, [selectedContact]);

    const fetchMessages = async (contactId) => {
        try {
            const response = await axios.get(`http://localhost:3001/messages/${userId}/${contactId}`);
            setMessages(response.data);
            // Add the contact ID to existing chats if there are messages
            if (response.data.length > 0) {
                setExistingChats(prev => new Set(prev).add(contactId));
            }
        } catch (error) {
            console.error('Error fetching messages:', error);
        }
    };

    const handleSendMessage = async () => {
        if (!selectedContact || newMessage.trim() === '') return;

        try {
            await axios.post('http://localhost:3001/messages/send', {
                senderId: userId,
                receiverId: selectedContact.id,
                message: newMessage,
            });
            const newMessageObj = {
                id: Date.now(), // Generate a unique temporary ID (replace with actual ID from server if available)
                senderId: userId,
                receiverId: selectedContact.id,
                message: newMessage,
            };
            setMessages([...messages, newMessageObj]);
            setNewMessage('');
            // Ensure the contact is added to existing chats when a message is sent
            setExistingChats(prev => new Set(prev).add(selectedContact.id));
        } catch (error) {
            console.error('Error sending message:', error);
        }
    };

    const filteredContacts = contacts.filter(
        contact =>
        (existingChats.has(contact.id) &&
            (contact.firstName.toLowerCase().includes(searchTerm.toLowerCase()) ||
                contact.lastName.toLowerCase().includes(searchTerm.toLowerCase()) ||
                contact.email.toLowerCase().includes(searchTerm.toLowerCase())))
    );

    return (
        <div className="messages-container">
            <div className="chat-list">
                <input
                    type="text"
                    placeholder="Search contacts..."
                    value={searchTerm}
                    onChange={(e) => setSearchTerm(e.target.value)}
                />
                {filteredContacts.map((contact) => (
                    <div
                        key={contact.id}
                        className={`chat-item ${selectedContact && selectedContact.id === contact.id ? 'selected' : ''}`}
                        onClick={() => setSelectedContact(contact)}
                    >
                        {contact.firstName} {contact.lastName}
                    </div>
                ))}
            </div>
            <div className="chat-messages">
                {selectedContact ? (
                    <>
                        <div className="messages">
                            {messages.map((msg) => (
                                <div key={msg.id} className={`message ${msg.senderId === userId ? 'sent' : 'received'}`}>
                                    {msg.message}
                                </div>
                            ))}
                        </div>
                        <div className="message-input">
                            <input
                                type="text"
                                value={newMessage}
                                onChange={(e) => setNewMessage(e.target.value)}
                                placeholder="Type your message..."
                            />
                            <button onClick={handleSendMessage}>Send</button>
                        </div>
                    </>
                ) : (
                    <div className="no-chat-selected">Select a contact to start messaging</div>
                )}
            </div>
        </div>
    );
};

export default Messages;
