import React, { useState, useEffect } from "react";
import {
    Accordion,
    AccordionItem,
    AccordionButton,
    AccordionPanel,
    AccordionIcon,
    Box,
    Heading,
    Text,
    Button,
    Stack,
    Image,
    extendTheme,
    ChakraProvider,
    FormControl,
    FormLabel,
    Input,
    Textarea,
    Select,
} from "@chakra-ui/react";
import axios from "axios";

const theme = extendTheme({
    styles: {
        global: {
            body: {
                color: "white",
                bg: "black",
            },
        },
    },
    components: {
        Accordion: {
            baseStyle: {
                container: {
                    borderTopColor: "white",
                },
                button: {
                    color: "white",
                },
                panel: {
                    color: "white",
                },
                icon: {
                    color: "white",
                },
            },
        },
        Box: {
            baseStyle: {
                bg: "whiteAlpha.200",
                borderColor: "white",
            },
        },
        Heading: {
            baseStyle: {
                color: "white",
            },
        },
        Text: {
            baseStyle: {
                color: "white",
            },
        },
        Button: {
            baseStyle: {
                color: "white",
                borderColor: "white",
                _hover: {
                    bg: "whiteAlpha.300",
                },
            },
        },
    },
});

const JobOpenings = () => {
    const [jobsByCategory, setJobsByCategory] = useState({});
    const [newJob, setNewJob] = useState({
        category: "",
        title: "",
        company: "",
        prerequisites: "",
        expectations: "",
        apply_link: "",
    });
    const [showForm, setShowForm] = useState(false); // State to toggle form visibility

    useEffect(() => {
        // Fetch all jobs on component mount
        axios.get("http://localhost:3001/jobs/all").then(response => {
            const jobs = response.data;

            // Categorize jobs by their category
            const categorizedJobs = jobs.reduce((acc, job) => {
                if (!acc[job.category]) {
                    acc[job.category] = [];
                }
                acc[job.category].push(job);
                return acc;
            }, {});

            setJobsByCategory(categorizedJobs);
        });
    }, []);

    const handleChange = (e) => {
        setNewJob({
            ...newJob,
            [e.target.name]: e.target.value,
        });
    };

    const handleSubmit = (e) => {
        e.preventDefault();
        axios.post("http://localhost:3001/jobs/add", newJob).then(() => {
            // Re-fetch all jobs after adding a new one
            axios.get("http://localhost:3001/jobs/all").then(response => {
                const jobs = response.data;

                // Re-categorize jobs by their category
                const categorizedJobs = jobs.reduce((acc, job) => {
                    if (!acc[job.category]) {
                        acc[job.category] = [];
                    }
                    acc[job.category].push(job);
                    return acc;
                }, {});

                setJobsByCategory(categorizedJobs);
                setShowForm(false); // Hide the form after submission
            });
        });
    };

    return (
        <ChakraProvider theme={theme}>
            <div>
                <Heading
                    fontFamily="Montserrat, sans-serif"
                    fontWeight="bold"
                    fontSize="50px"
                    textAlign="center"
                    mt={8}
                    mb={4}
                >
                    Job Openings
                </Heading>

                <Box p={6}>
                    <Button
                        onClick={() => setShowForm(!showForm)}
                        colorScheme="purple"
                        variant="solid"
                        mb={4}
                    >
                        {showForm ? "Hide Job Form" : "Create a New Job Listing"}
                    </Button>

                    {showForm && (
                        <form onSubmit={handleSubmit}>
                            <FormControl mb={4}>
                                <FormLabel>Category</FormLabel>
                                <Select name="category" onChange={handleChange} required>
                                    <option value="">Select Category</option>
                                    <option value="Dance">Dance</option>
                                    <option value="Music - Classical">Music - Classical</option>
                                    <option value="Music - Contemporary">Music - Contemporary</option>
                                    <option value="Visual Art">Visual Art</option>
                                    <option value="Theatre">Theatre</option>
                                    <option value="Film & Literature">Film & Literature</option>
                                </Select>
                            </FormControl>
                            <FormControl mb={4}>
                                <FormLabel>Title</FormLabel>
                                <Input name="title" onChange={handleChange} required />
                            </FormControl>
                            <FormControl mb={4}>
                                <FormLabel>Company</FormLabel>
                                <Input name="company" onChange={handleChange} required />
                            </FormControl>
                            <FormControl mb={4}>
                                <FormLabel>Pre-requisites</FormLabel>
                                <Textarea name="prerequisites" onChange={handleChange} />
                            </FormControl>
                            <FormControl mb={4}>
                                <FormLabel>Expectations</FormLabel>
                                <Textarea name="expectations" onChange={handleChange} />
                            </FormControl>
                            <FormControl mb={4}>
                                <FormLabel>Link to Apply</FormLabel>
                                <Input name="apply_link" onChange={handleChange} />
                            </FormControl>
                            <Button type="submit" colorScheme="purple" variant="solid">
                                Create Job Listing
                            </Button>
                        </form>
                    )}

                    <Accordion allowToggle mt={8}>
                        {Object.keys(jobsByCategory).map((category) => (
                            <AccordionItem key={category}>
                                <h2>
                                    <AccordionButton>
                                        <Box flex="1" textAlign="left">
                                            <Heading fontSize="xl">{category}</Heading>
                                        </Box>
                                        <AccordionIcon />
                                    </AccordionButton>
                                </h2>
                                <AccordionPanel pb={4}>
                                    <Stack spacing={4}>
                                        {jobsByCategory[category].map((job, index) => (
                                            <Box
                                                key={index}
                                                borderWidth="1px"
                                                borderRadius="lg"
                                                p={4}
                                                display="flex"
                                                alignItems="center"
                                                backgroundColor="whitesmoke"

                                            >
                                                <Image
                                                    src="/logo.jpg"
                                                    alt="Company Logo"
                                                    boxSize="150px"
                                                    objectFit="cover"
                                                    mr={4}
                                                />
                                                <Stack flex="1">
                                                    <Stack direction="row" align="center" mb={2} >
                                                        <Heading fontSize="lg" color="black">{job.title}</Heading>
                                                        <Text fontSize="sm" color="gray.300" ml={2}>
                                                            at {job.company}
                                                        </Text>
                                                    </Stack>
                                                    <Stack spacing={2}>
                                                        <Text color="black">
                                                            <strong>Pre-requisites:</strong>{" "}
                                                            {job.prerequisites}
                                                        </Text>
                                                        <Text color="black">
                                                            <strong>Expectations:</strong>{" "}
                                                            {job.expectations}
                                                        </Text>
                                                        <Text color="black">
                                                            <strong>Apply Here:</strong>{" "}
                                                            <a href={job.apply_link} target="_blank" rel="noopener noreferrer">
                                                                {job.apply_link}
                                                            </a>
                                                        </Text>
                                                    </Stack>
                                                    <Button
                                                        mt={4}
                                                        colorScheme="purple"
                                                        variant="solid"
                                                    >
                                                        Apply for Job
                                                    </Button>
                                                </Stack>
                                            </Box>
                                        ))}
                                    </Stack>
                                </AccordionPanel>
                            </AccordionItem>
                        ))}
                    </Accordion>
                </Box>
            </div>
        </ChakraProvider>
    );
};

export default JobOpenings;
