import React from 'react';
import { Tabs, TabList, TabPanels, Tab, TabPanel, Card, Image, Stack, CardBody, Heading, Text, Box, Link } from '@chakra-ui/react';

const EventsDiscovery = () => {
    const eventsData = [
        {
            category: 'Dance',
            events: [
                {
                    title: 'Dance Competition 2024',
                    description: 'Join the annual dance competition to showcase your talent and win amazing prizes. This event is perfect for solo performers and groups looking for a challenge.',
