                },
                {
                    title: 'Salsa Night',
                    description: 'Experience the vibrant world of salsa dancing. Open to all skill levels, come and enjoy a night filled with music and dance.',
                    time: 'August 10, 2024, 7:00 PM',
                    place: 'Downtown Dance Studio',
                    rsvp: 'https://example.com/rsvp-dance2',
                    image: 'https://images.unsplash.com/photo-1566902249079-c97d67671278?w=800&auto=format&fit=crop&q=60&ixlib=rb-4.0.3&ixid=M3wxMjA3fDB8MHxzZWFyY2h8MXx8U2Fsc2ElMjBOaWdodHxlbnwwfHwwfHx8MA%3D%3D'
                },
                {
                    title: 'Hip Hop Jam',
                    description: 'A thrilling night of hip hop dance battles. Show off your best moves and compete with the best dancers in town.',
                    time: 'July 22, 2024, 8:00 PM',
                    place: 'Urban Dance Club',
                    rsvp: 'https://example.com/rsvp-dance3',
                    image: 'https://images.unsplash.com/photo-1597699411413-2b1e1a9dfab6'
                },
                {
                    title: 'Ballet Showcase',
                    description: 'A beautiful ballet performance featuring classical and contemporary pieces performed by talented dancers.',
                    time: 'August 5, 2024, 6:30 PM',
                    place: 'City Ballet Theater',
                    rsvp: 'https://example.com/rsvp-dance4',
                    image: 'https://images.unsplash.com/photo-1597023054663-d3f9a5d76b6f'
                },
                {
                    title: 'Latin Dance Festival',
                    description: 'A festival celebrating Latin dance styles including salsa, bachata, and merengue. Dance workshops and performances all day long.',
                    time: 'July 29, 2024, 12:00 PM - 10:00 PM',
                    place: 'Cultural Arts Center',
                    rsvp: 'https://example.com/rsvp-dance5',
                    image: 'https://images.unsplash.com/photo-1581175305340-4ac30d0fe867'
                }
            ]
        },
        {
            category: 'Music - Classical',
            events: [
                {
                    title: 'Classical Music Evening',
                    description: 'An evening of enchanting classical music performances by local and guest artists. Enjoy a night of symphonies and solos.',
                    time: 'July 20, 2024, 8:00 PM',
                    place: 'Grand Concert Hall',
                    rsvp: 'https://example.com/rsvp-music-classical1',
                    image: 'https://example.com/classical1.png'
                },
                {
                    title: 'Piano Recital',
                    description: 'A solo piano recital by renowned pianist John Doe. Experience the beauty of classical piano compositions in an intimate setting.',
                    time: 'August 5, 2024, 7:30 PM',
                    place: 'City Auditorium',
                    rsvp: 'https://example.com/rsvp-music-classical2',
                    image: 'https://example.com/classical2.png'
                },
                {
                    title: 'String Quartet Performance',
                    description: 'A delightful evening featuring a string quartet performing classical and contemporary pieces.',
                    time: 'July 25, 2024, 7:00 PM',
                    place: 'Chamber Music Hall',
                    rsvp: 'https://example.com/rsvp-music-classical3',
                    image: 'https://example.com/classical3.png'
                },
                {
                    title: 'Opera Night',
                    description: 'An evening of arias and duets performed by talented opera singers. Enjoy the dramatic and beautiful world of opera.',
                    time: 'August 10, 2024, 6:00 PM',
                    place: 'Opera House',
                    rsvp: 'https://example.com/rsvp-music-classical4',
                    image: 'https://example.com/classical4.png'
                }
            ]
        },
        {
            category: 'Music - Contemporary',
            events: [
                {
                    title: 'Rock Concert',
                    description: 'Get ready to rock! Join us for an electrifying concert featuring the best local rock bands.',
                    time: 'July 25, 2024, 9:00 PM',
                    place: 'Open Air Theater',
                    rsvp: 'https://example.com/rsvp-contemporary1',
                    image: '/contemporary1.png'
                },
                {
                    title: 'Pop Music Fest',
                    description: 'A festival celebrating pop music with performances by various artists. Enjoy a day full of music, food, and fun.',
                    time: 'August 12, 2024, 3:00 PM',
                    place: 'City Park',
                    rsvp: 'https://example.com/rsvp-contemporary2',
                    image: '/contemporary2.png'
                }
            ]
        },
        {
            category: 'Visual Art',
            events: [
                {
                    title: 'Art Exhibition',
                    description: 'An exhibition showcasing contemporary artworks by local artists. Come and appreciate the creativity and talent of our community.',
                    time: 'July 30, 2024, 10:00 AM - 6:00 PM',
                    place: 'Art Gallery',
                    rsvp: 'https://example.com/rsvp-visual1',
                    image: '/art1.png'
                },
                {
                    title: 'Street Art Festival',
                    description: 'A vibrant street art festival featuring live mural painting, art installations, and workshops.',
                    time: 'August 20, 2024, 11:00 AM - 5:00 PM',
                    place: 'Downtown Streets',
                    rsvp: 'https://example.com/rsvp-visual2',
                    image: '/art2.png'
                }
            ]
        },
        {
            category: 'Theatre',
            events: [
                {
                    title: 'Shakespeare in the Park',
                    description: 'Enjoy a classic Shakespeare play performed in the beautiful surroundings of the park. Bring your picnic and enjoy the show.',
                    time: 'August 1, 2024, 6:00 PM',
                    place: 'Central Park',
                    rsvp: 'https://example.com/rsvp-theatre1',
                    image: '/theatre1.png'
                },
                {
                    title: 'Modern Play Night',
                    description: 'An evening of contemporary theatre with performances by emerging playwrights and actors.',
                    time: 'August 18, 2024, 7:00 PM',
                    place: 'Community Theatre',
                    rsvp: 'https://example.com/rsvp-theatre2',
                    image: '/theatre2.png'
                }
            ]
        },
        {
            category: 'Film & Literature',
            events: [
                {
                    title: 'Outdoor Movie Night',
                    description: 'Watch a classic film under the stars. Free popcorn and drinks will be provided.',
                    time: 'July 28, 2024, 8:00 PM',
                    place: 'Riverside Park',
                    rsvp: 'https://example.com/rsvp-film1',
                    image: '/film1.png'
                },
                {
                    title: 'Author Meet & Greet',
                    description: 'Meet your favorite authors, get your books signed, and attend live readings.',
                    time: 'August 15, 2024, 2:00 PM - 4:00 PM',
                    place: 'City Library',
                    rsvp: 'https://example.com/rsvp-literature1',
                    image: '/literature1.png'
                }
            ]
        }
    ];

    return (
        <div>
            <Heading fontFamily='Montserrat, sans-serif' fontWeight='bold' color='white' fontSize='50px' textAlign='center' mt={8} mb={4}>
                Events
            </Heading>
            <br />

            <Tabs variant='soft-rounded'>
                <TabList bg='purple.200' borderRadius='xl' p={4} mb={4}>
                    {eventsData.map((category, index) => (
                        <Tab key={index} _selected={{ color: 'white', bg: 'purple.700' }} _focus={{ boxShadow: 'none' }} _hover={{ bg: 'purple.500' }} mr={2}>
                            {category.category}
                        </Tab>
                    ))}
                </TabList>
                <TabPanels>
                    {eventsData.map((category, index) => (
                        <TabPanel key={index}>
                            <div>
                                {category.events.length > 0 ? (
                                    category.events.map((event, i) => (
                                        <div key={i}>
                                            <Card direction={{ base: 'column', sm: 'row' }} overflow='hidden' variant='elevated' bg="rgba(255, 255, 255, 0.8)" maxW="97%" padding="20px" borderRadius={"50px"} mb={4}>
                                                <Image
                                                    objectFit='cover'
                                                    maxW={{ base: '50%', sm: '50%' }}
                                                    src={event.image}
                                                    alt={event.title}
                                                    height='300px' // Set the height to a fixed value
                                                    width='500px' // Set the width to a fixed value
                                                />
                                                <Stack>
                                                    <CardBody padding="10px">
                                                        <Heading size='lg'>{event.title}</Heading>
                                                        <Text py='4' fontSize="18px" fontFamily={'Montserrat, sans-serif'} fontWeight={"500"}>
                                                            {event.description}
                                                        </Text>
                                                        <Text fontSize="16px" fontFamily={'Montserrat, sans-serif'} fontWeight={"500"}>
                                                            <strong>Time:</strong> {event.time}
                                                        </Text>
                                                        <Text fontSize="16px" fontFamily={'Montserrat, sans-serif'} fontWeight={"500"}>
                                                            <strong>Place:</strong> {event.place}
                                                        </Text>
                                                        <Link href={event.rsvp} color='purple.500' isExternal>
                                                            RSVP Here
                                                        </Link>
                                                    </CardBody>
                                                </Stack>
                                            </Card>
                                        </div>
                                    ))
                                ) : (
                                    <h3 style={{ color: 'white' }}>No events scheduled.</h3>
                                )}
                            </div>
                        </TabPanel>
                    ))}
                </TabPanels>
            </Tabs>
        </div>
    );
}

export default EventsDiscovery;
