import React, { useState, useEffect } from 'react';
import Button from '@mui/material/Button';
import Avatar from '@mui/material/Avatar';
import axios from 'axios';
import { ChakraProvider } from '@chakra-ui/react';
import ConnectionForm from './ConnectionForm';
import Messages from './Messages'; // Updated import for Messages component
import EventsDiscovery from './EventsDiscovery';
import JobOpenings from './JobOpenings';
import PendingRequests from './PendingRequests';

function HomePage({ user }) {
    const [currentPage, setCurrentPage] = useState(null);
    const [contacts, setContacts] = useState([]);
    const [selectedContact, setSelectedContact] = useState(null);

    useEffect(() => {
        // Fetch contacts when component mounts
        fetchContacts();
    }, []);

    const fetchContacts = async () => {
        try {
            const response = await axios.get(`http://localhost:3001/contacts/${user.id}`);
            setContacts(response.data);
            if (response.data.length > 0) {
                setSelectedContact(response.data[0]); // Select the first contact by default
            }
        } catch (error) {
            console.error('Error fetching contacts:', error);
        }
    };

    const handleConnectionsClick = () => {
        setCurrentPage('connections');
    };

    const handleEventsClick = () => {
        setCurrentPage('events');
    };

    const handleJobClick = () => {
        setCurrentPage('job');
    };

    const handleMessagesClick = () => {
        setCurrentPage('messages'); // Update state to show messages
    };

    const renderPage = () => {
        switch (currentPage) {
            case 'events':
                return (
                    <>
                        <Button
                            variant="outlined"
                            style={{ backgroundColor: '#9C27B0', color: 'white', borderColor: '#6A1B9A', marginLeft: '20px' }}
                            onClick={() => setCurrentPage(null)}
                        >
                            Back to Home
                        </Button>
                        <ChakraProvider>
                            <EventsDiscovery />
                        </ChakraProvider>
                    </>
                );
            case 'job':
                return (
                    <>
                        <Button
                            variant="outlined"
                            style={{ backgroundColor: '#9C27B0', color: 'white', borderColor: '#6A1B9A', marginLeft: '20px' }}
                            onClick={() => setCurrentPage(null)}
                        >
                            Back to Home
                        </Button>
                        <ChakraProvider>
                            <JobOpenings />
                        </ChakraProvider>
                    </>
                );
            case 'messages':
                return (
                    <>
                        <Button
                            variant="outlined"
                            style={{ backgroundColor: '#9C27B0', color: 'white', borderColor: '#6A1B9A', marginLeft: '20px' }}
                            onClick={() => setCurrentPage(null)}
                        >
                            Back to Home
                        </Button>
                        <div className="messages-container">
                            <div className="chat-list">
                                {contacts.map((contact) => (
                                    <div
                                        key={contact.id}
                                        className={`chat-item ${selectedContact && selectedContact.id === contact.id ? 'selected' : ''}`}
                                        onClick={() => setSelectedContact(contact)}
                                    >
                                        {contact.name}
                                    </div>
                                ))}
                            </div>
                            <div className="chat-messages">
                                <Messages userId={user.id} contacts={contacts} selectedContact={selectedContact} setSelectedContact={setSelectedContact} />
                            </div>
                        </div>
                    </>
                );
            case 'connections':
                return (
                    <>
                        <Button
                            variant="outlined"
                            style={{ backgroundColor: '#9C27B0', color: 'white', borderColor: '#6A1B9A', marginLeft: '20px' }}
                            onClick={() => setCurrentPage(null)}
                        >
                            Back to Home
                        </Button>
                        <ConnectionForm senderEmail={user.email} />
                        <PendingRequests userEmail={user.email} />
                    </>
                );
            default:
                return (
                    <>
                        <div style={{ backgroundColor: 'white', minHeight: '100vh', borderRadius: '10px', padding: '50px', display: 'flex', flexDirection: 'column', alignItems: 'center', fontFamily: 'Montserrat, sans-serif', color: 'white', width: '96vw', margin: '0 auto' }}>
                            <div style={{ textAlign: 'center', color: '#333', display: 'flex', alignItems: 'center' }}>
                                <Avatar
                                    src="/avatar.png"
                                    sx={{
                                        height: '250px',
                                        width: '250px',
                                        marginRight: '10vw',
                                    }} />

                                <div style={{ display: 'flex', flexDirection: 'column', alignItems: 'center', marginTop: '80px' }}>
                                    <h1 style={{ fontWeight: 'bold', fontSize: '2.75rem' }}>Welcome back {user ? `${user.firstName} ${user.lastName}` : ''}! </h1>
                                    <h2 style={{ fontSize: '1.7rem' }}>You have successfully logged in!</h2>
                                    <p style={{ fontSize: '1.2rem' }}>Feel free to enjoy the following fun features.</p>
                                    <br /> <br /> <br />
                                </div>
                            </div>
                            <hr color="#c1acd9" width="90%" style={{ marginBottom: "80px" }} />
                            <div style={{ display: 'flex', flexDirection: 'row', gap: '170px', justifyContent: 'center', marginBottom: '50px', paddingLeft: '40px' }}>
                                <img src="/events.png" alt="Events" style={{ width: '120px', height: '120px' }} />
                                <img src="/jobs.png" alt="Jobs" style={{ width: '120px', height: '120px' }} />
                                <img src="/messages.png" alt="Messages" style={{ width: '120px', height: '120px' }} />
                                <img src="/connections.png" alt="Connections" style={{ width: '120px', height: '120px' }} />
                            </div>
                            <div style={{ display: 'flex', flexDirection: 'row', gap: '30px', justifyContent: 'center' }}>
                                <Button
                                    variant='outlined'
                                    style={{
                                        fontSize: '1.7rem',
                                        borderRadius: '50px',
                                        fontFamily: 'Montserrat, sans-serif',
                                        color: '#3900FF',
                                        borderColor: '#3900FF',
                                        padding: '20px',
                                        boxShadow: '0px 4px 10px rgba(0, 0, 0, 0.1)'
                                    }}
                                    onClick={handleEventsClick}
                                >
                                    Events Discovery
                                </Button>
                                <Button
                                    variant='outlined'
                                    style={{
                                        fontSize: '1.7rem',
                                        borderRadius: '50px',
                                        fontFamily: 'Montserrat, sans-serif',
                                        color: '#9C27B0',
                                        borderColor: '#9C27B0',
                                        padding: '20px',
                                        boxShadow: '0px 4px 10px rgba(0, 0, 0, 0.1)'
                                    }}
                                    onClick={handleJobClick}
                                >
                                    Job Openings
                                </Button>
                                <Button
                                    variant='outlined'
                                    style={{
                                        fontSize: '1.7rem',
                                        borderRadius: '50px',
                                        fontFamily: 'Montserrat, sans-serif',
                                        color: '#FF5700',
                                        borderColor: '#FF5700',
                                        padding: '20px',
                                        boxShadow: '0px 4px 10px rgba(0, 0, 0, 0.1)'
                                    }}
                                    onClick={handleMessagesClick}
                                >
                                    Messages
                                </Button>
                                <Button
                                    variant='outlined'
                                    style={{
                                        fontSize: '1.7rem',
                                        borderRadius: '50px',
                                        fontFamily: 'Montserrat, sans-serif',
                                        color: '#FF0097',
                                        borderColor: '#FF0097',
                                        padding: '20px',
                                        boxShadow: '0px 4px 10px rgba(0, 0, 0, 0.1)'
                                    }}
                                    onClick={handleConnectionsClick}
                                >
                                    Connections
                                </Button>
                            </div>
                            <div style={{ display: 'flex', flexDirection: 'row', textAlign: 'center', fontFamily: 'Montserrat, sans-serif', color: 'black', marginTop: '40px' }}>
                                <p>Explore features to message other artists or potential employers, connect with others, apply for jobs, and discover events!</p>
                            </div>
                        </div>
                    </>
                );
        }
    };

    return <>{renderPage()}</>;
}

export default HomePage;
