import { useState, React, useEffect } from 'react'
import axios from "axios";

const Messages = () => {
    const [currentChat, setCurrentChat] = useState(null);
    const [inputText, setInputText] = useState('');

    const users = [
        { id: 1, name: 'Alice' },
        { id: 2, name: 'Bob' },
        { id: 3, name: 'Charlie' },
    ];

    const messages = {
        1: [
            { id: 1, sender: 'Alice', text: 'Hi there!' },
            { id: 2, sender: 'User', text: 'Hello!' },
        ],
        2: [
            { id: 1, sender: 'Bob', text: 'How are you?' },
            { id: 2, sender: 'User', text: 'I am good, thanks!' },
        ],
        3: [
            { id: 1, sender: 'Charlie', text: 'Hey!' },
            { id: 2, sender: 'User', text: 'Hey Charlie!' },
        ],
    };

    const selectChat = (userId) => {
        setCurrentChat(userId);
    };

    const sendMessage = (e) => {
        e.preventDefault();
        if (currentChat && inputText.trim()) {
            const newMessage = {
                id: messages[currentChat].length + 1,
                sender: 'User',
                text: inputText,
            };
            messages[currentChat].push(newMessage);
            setInputText('');
        }
    };

    return (
        <div style={{ display: 'flex', height: '100vh', borderRadius: '20px' }}>
            <div style={{ width: '25%', backgroundColor: '#f0f0f0', borderRight: '1px solid #ddd', padding: '10px', borderRadius: '20px' }}>
                <h2 style={{ fontFamily: 'Montserrat, sans-serif' }}>Users</h2>
                {users.map(user => (
                    <div
                        key={user.id}
                        onClick={() => selectChat(user.id)}
                        style={{
                            padding: '10px',
                            cursor: 'pointer',
                            backgroundColor: currentChat === user.id ? '#ddd' : 'transparent',
                            borderRadius: '5px',
                            marginBottom: '5px',
                        }}
                    >
                        {user.name}
                    </div>
                ))}
            </div>
            <div style={{ flex: 1, backgroundColor: '#fff', display: 'flex', flexDirection: 'column', padding: '10px', borderRadius: '20px' }}>
                <div style={{ flex: 1, overflowY: 'auto', marginBottom: '10px' }}>
                    {currentChat ? (
                        messages[currentChat].map(message => (
                            <div key={message.id} style={{ margin: '10px 0' }}>
                                <strong>{message.sender}:</strong> {message.text}
                            </div>
                        ))
                    ) : (
                        <div style={{ textAlign: 'center', marginTop: '20px' }}>Select a user to start chatting</div>
                    )}
                </div>
                {currentChat && (
                    <form onSubmit={sendMessage} style={{ display: 'flex' }}>
                        <input
                            type="text"
                            value={inputText}
                            onChange={(e) => setInputText(e.target.value)}
                            placeholder="Type your message..."
                            style={{ flex: 1, padding: '10px', borderRadius: '5px', border: '1px solid #ccc', marginRight: '10px' }}
                        />
                        <button type="submit" style={{ padding: '10px 20px', borderRadius: '5px', border: 'none', backgroundColor: '#007BFF', color: 'white', cursor: 'pointer' }}>Send</button>
                    </form>
                )}
            </div>
        </div>
    );
};




export default Messages;
